var Frant = function(express)
{
	this.express = express || this.createExpressApp();

	this.routes = {};
	this.models = {};
	this.views  = {};
	this.ModelBase = require('ModelBase');
}

Frant.prototype =
{
	createExpressApp: function()
	{
		var express = require('express');
		return express();
	},

	translateRoutes: function()
	{
		for(var route in this.routes)
		{
			for(var method in route)
			{
				this.express[method](route, this.routes[route][method]);
			}
		}
	},

	listen: function(port)
	{
		this.express.listen(port);
	},

	// HELPERS
	RESTful: function(value)
	{
		this.routes[value] = this.routes[value] || {};
		this.routes[value]['get'] = {name: "Luiz"};
		// Code here...
	}
};


module.exports = Frant;
